unction"},{"inputs":[{"internalType":"uint256","name":"_gasCap","type":"uint256"}],"name":"setGasCap","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"_mirrorConnector","type":"address"}],"name":"setMirrorConnector","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"_expected","type":"address"}],"name":"verifySender","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"nonpayable","type":"function"},{"stateMutability":"payable","type":"receive"}],"devdoc":{"kind":"dev","methods":{"processMessage(bytes)":{"details":"This is called by AMBs to process messages originating from mirror connector"},"receiveWormholeMessages(bytes,bytes[],bytes32,uint16,bytes32)":{"params":{"additionalVaas":"- Additional VAAs which were requested to be included in this delivery.   They are guaranteed to all be included and in the same order as was specified in the     delivery request.","deliveryHash":"- the VAA hash of the deliveryVAA. NOTE: These signedVaas are NOT verified by the Wormhole core contract prior to being provided     to this call. Always make sure `parseAndVerify()` is called on the Wormhole core contract     before trusting the content of a raw VAA, otherwise the VAA may be invalid or malicious.","payload":"- an arbitrary message which was included in the delivery by the     requester.","sourceAddress":"- the (wormhole format) address on the sending chain which requested     this delivery.","sourceChain":"- the wormhole chain ID where this delivery was requested."}},"sendMessage(bytes,bytes)":{"details":"This is called by the root manager *only* on mainnet to propagate the aggregate root"}},"version":1},"userdoc":{"events":{"GasCapUpdated(uint256,uint256)":{"notice":"Emitted when admin updates the gas cap"},"MessageProcessed(bytes,address)":{"notice":"Emitted whenever a message is successfully received over an AMB"},"MessageSent(bytes